<?if(!defined("B_PROLOG_INCLUDED") || B_PROLOG_INCLUDED!==true)die();/** @var array $arParams *//** @var array $arResult *//** @global CMain $APPLICATION *//** @global CUser $USER *//** @global CDatabase $DB *//** @var CBitrixComponentTemplate $this *//** @var string $templateName *//** @var string $templateFile *//** @var string $templateFolder *//** @var string $componentPath *//** @var CBitrixComponent $component */$this->setFrameMode(true);?><div class="container">    <ol class="breadcrumb">        <li class="breadcrumb-item"><a href="/"><?=$arResult["FIELDS"]["IBLOCK_NAME"];?></a></li>        <li class="breadcrumb-item active"><?=$arResult["NAME"]?></li>    </ol>    <?if($arResult['CREATED_BY'] != $USER->GetID() && $USER->IsAuthorized() && !CSite::InGroup(5)):?>    <div class="row">        <div class="col-12 col-md-3 push-md-9 bd-sidebar">            <?                if(getListAdd(['IBLOCK_ID' => '4', 'IBLOCK_SECTION_ID' => '1'], $arResult['ID'])):            ?>                <button type="button" class="btn btn-primary btn-block" data-target="#myModal" data-toggle="modal">Откликнуться</button>                <div class="modal fade" id="myModal" tabindex="-1" role="dialog" aria-hidden="true">                    <div class="modal-dialog" role="document">                        <div class="modal-content">                            <div class="modal-header">                                <h5 class="modal-title">Отклик на вакансию</h5>                                <button type="button" class="close" data-dismiss="modal" aria-label="Close">                                    <span aria-hidden="true">&times;</span>                                </button>                            </div>                            <div class="modal-body">                            </div>                        </div>                    </div>                </div>                <script>                    'use strict'                    class Darkness {                        constructor(element) {                            this.element = element;                        }                        static init(element) {                            return new Darkness(element);                        }                        enigmaForm() {                            const form = $(this.element.form),                                  DescMail = form.find('textarea'),                                  JobID = form.find('input');                            form.on('submit', function(e) {                                e.preventDefault();                                if(DescMail.val() && Number(JobID.val()))                                {                                    $.post(                                        '/job/zayavka.php',                                        {                                            JobID: JobID.val(),                                            DescMail: DescMail.val()                                        },                                        function (html) {                                            $('.modal-body').html(html);                                        }                                    );                                }                                else {                                    $('.modal-body .modal-error').html('<div class="alert alert-danger" role="alert">Произошла ошибка при отправе данный!</div>');                                    DescMail.focus();                                }                            });                            return this;                        }                        enigmaModalMod() {                            const _this = this;                            $(this.element.modal).on('hidden.bs.modal', function () {                                $('.modal-body').html('<div class="progress">\n' +                                    '    <div class="progress-bar progress-bar-striped progress-bar-animated" role="progressbar" aria-valuenow="100" aria-valuemin="0" aria-valuemax="100" style="width: 100%"></div>\n' +                                    '  </div>');                            }).on('shown.bs.modal', function() {                                $('.modal-body').html('<div class="progress">\n' +                                    '    <div class="progress-bar progress-bar-striped progress-bar-animated" role="progressbar" aria-valuenow="100" aria-valuemin="0" aria-valuemax="100" style="width: 100%"></div>\n' +                                    '  </div>');                                $.post(                                    '/job/zayavka.php',                                    {                                        JobID: <?=$arResult['ID'];?>                                    },                                    function (html) {                                        $('.modal-body').html(html);                                        _this.enigmaForm();                                    }                                );                            });                            return this;                        }                    }                    (function() {                        let euclihel = Darkness.init({"form":"form", "modal":"#myModal"});                        euclihel.enigmaModalMod();                    })();                </script>                <?else:?>                    <button type="button" class="btn btn-success btn-block">Вы уже откликнулись</button>                <?endif;?>            </div>        <div class="col-12 col-md-9 pull-md-3 bd-content">            <?endif;?>            <div class="job-description">                <div class="jumbotron">                    <h1 class="display-4"><?=$arResult["NAME"]?></h1>                    <p class="lead">                        <?foreach($arResult["DISPLAY_PROPERTIES"] as $pid => $arProperty):?>                            <b><?=$arProperty["NAME"]?>:</b>                            <?if(is_array($arProperty["DISPLAY_VALUE"])):?>                                <?=implode("&nbsp;/&nbsp;", $arProperty["DISPLAY_VALUE"]);?>                            <?else:?>                                <?=$arProperty["DISPLAY_VALUE"];?><br>                            <?endif?>                        <?endforeach;?>                    </p>                </div>            </div>        </div>    </div></div>