<?php    use Bitrix\Main\Context;    function getListAdd($arParams, $id)    {        $rsFindEl = CIBlockElement::GetList(            [                'SORT' => 'ASC',            ],            [                'IBLOCK_ID' => $arParams['IBLOCK_ID'],                'IBLOCK_SECTION_ID ' => $arParams['IBLOCK_SECTION_ID'],                'CREATED_BY' => $GLOBALS['USER']->GetID(),                'PROPERTY_JobID' => $id,                'ACTIVE' => 'Y'            ]        );        if(!$rsFindEl->Fetch())            return true;        else            return false;    }?>